mapper.select:
SELECT *
FROM iot_device_log


        AND product_key = #{reqVO.productKey}


        AND device_key = #{reqVO.deviceKey}


        AND type = #{reqVO.type}


        AND identifier = #{reqVO.identifier}


        AND ts >= #{reqVO.createTime[0]}
        AND ts <= #{reqVO.createTime[1]}


ORDER BY ts DESC

SELECT COUNT(*)
FROM device_log


        AND ts >= #{createTime}

SELECT
DATE_FORMAT(ts, '%Y-%m-%d %H:00:00') as time,
COUNT(*) as data
FROM device_log


        AND device_key = #{deviceKey}


        AND ts >= #{startTime}


        AND ts <= #{endTime}

    AND identifier IN ('online', 'offline', 'pull', 'progress', 'report', 'register', 'register_sub')

GROUP BY DATE_FORMAT(ts, '%Y-%m-%d %H:00:00')
ORDER BY time ASC

SELECT
DATE_FORMAT(ts, '%Y-%m-%d %H:00:00') as time,
COUNT(*) as data
FROM device_log


        AND device_key = #{deviceKey}


        AND ts >= #{startTime}


        AND ts <= #{endTime}

    AND identifier IN ('set', 'get', 'upgrade', 'unregister_sub', 'topology_add')

GROUP BY DATE_FORMAT(ts, '%Y-%m-%d %H:00:00')
ORDER BY time ASC
